import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001'); // Substitua pelo URL do seu servidor

const CarSimulator = ({ carName, count, onIncrement, onDecrement }) => {
  return (
    <div className="car-simulator">
      <h3>{carName}: {count}</h3>
      <div className="buttons">
        <button onClick={onIncrement}>+</button>
        <button onClick={onDecrement}>-</button>
      </div>
    </div>
  );
};

function App() {
  const [counters, setCounters] = useState({
    carros: [
      { id: 'porsche', name: 'Porsche', count: 0 },
      { id: 'ferrari', name: 'Ferrari', count: 0 },
      { id: 'mercedes', name: 'Mercedes', count: 0 },
    ],
    videos: [
      { id: 'video1', name: 'Video 1', count: 0 },
      { id: 'video2', name: 'Video 2', count: 0 },
      { id: 'video3', name: 'Video 3', count: 0 },
    ],
    carrosProfissionais: [
      { id: 'porsche', name: 'Porsche', count: 0 },
      { id: 'mclaren', name: 'McLaren', count: 0 },
      { id: 'aviao', name: 'Avião', count: 0 },
    ],
  });

  // Atualiza os contadores ao receber dados do servidor
  useEffect(() => {
    socket.on('counterUpdate', (updatedCounters) => {
      setCounters(updatedCounters);
    });

    return () => {
      socket.off('counterUpdate');
    };
  }, []);

  // Funções para incrementar e decrementar contadores
  const handleIncrement = (category, id) => {
    socket.emit('updateCounter', { category, id, action: 'increment' });
  };

  const handleDecrement = (category, id) => {
    socket.emit('updateCounter', { category, id, action: 'decrement' });
  };

  return (
    <div className="App">
      <h1>Térreo</h1>
      
      {/* Seções renderizadas dinamicamente */}
      {Object.entries(counters).map(([category, items]) => (
        <div key={category} style={{ margin: '20px' }}>
          <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
          <div style={{ marginLeft: '20px' }}>
            {items.map((item) => (
              <CarSimulator
                key={item.id}
                carName={item.name}
                count={item.count}
                onIncrement={() => handleIncrement(category, item.id)}
                onDecrement={() => handleDecrement(category, item.id)}
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
