import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3001');

function App() {
  const [porscheCount, setPorscheCount] = useState(0);
  const [formulaCount, setFormulaCount] = useState(0);
  const [mercedesCount, setMercedesCount] = useState(0);

  const incrementPorsche = () => {
    socket.emit("increment_porsche");
  };
  const decrementPorsche = () => {
    socket.emit("decrement_porsche");
  };

  const incrementFormula = () => {
    socket.emit("increment_formula");
  };
  const decrementFormula = () => {
    socket.emit("decrement_formula");
  };

  const incrementMercedes = () => {
    socket.emit("increment_mercedes");
  };
  const decrementMercedes = () => {
    socket.emit("decrement_mercedes");
  };

  useEffect(() => {
    socket.on("receive_message", (data) => {
      setPorscheCount(data.porscheCount);
      setFormulaCount(data.formulaCount);
      setMercedesCount(data.mercedesCount);
    });

    // Cleanup: Remove o ouvinte quando o componente for desmontado
    return () => {
      socket.off("receive_message");
    };
  }, []);

  return (
    <div className="App">
      <h1>Térreo</h1>
      <div>
        <h2>Simuladores de Carro:</h2>
        <div>
          <h3>Porsche: {porscheCount}</h3>
          <button onClick={incrementPorsche}>+</button>
          <button onClick={decrementPorsche}>-</button>
        </div>
        <div>
          <h3>Fórmula: {formulaCount}</h3>
          <button onClick={incrementFormula}>+</button>
          <button onClick={decrementFormula}>-</button>
        </div>
        <div>
          <h3>Mercedes: {mercedesCount}</h3>
          <button onClick={incrementMercedes}>+</button>
          <button onClick={decrementMercedes}>-</button>
        </div>
      </div>
    </div>
  );
}

export default App;
