import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

// URL do servidor
const socket = io('http://localhost:3001');

function App() {
  // Estado para armazenar o contador
  const [count, setCount] = useState(0);

  // Função de incremento
  const increment = () => {
    socket.emit("increment"); // Envia um evento de incremento
  }

  // Função de decremento
  const decrement = () => {
    socket.emit("decrement"); // Envia um evento de decremento
  }

  useEffect(() => {
    // Escutando o evento de atualização do contador
    socket.on("receive_message", (data) => {
      setCount(data.count); // Atualiza o estado do contador com o valor recebido
    });

    // Cleanup: Remover o ouvinte quando o componente for desmontado
    return () => {
      socket.off("receive_message");
    };
  }, []);

  return (
    <div className="App">
      <h1>Contador: {count}</h1>
      <button onClick={increment}>+</button>
      <button onClick={decrement}>-</button>
    </div>
  );
}

export default App;
