import "./App.css"
import io from "socket.io-client"
import { useEffect } from "react"

const socket = io.connect("http://localhost:3001")
let count = 1

function App() {
  const sendMessage = () => {
    socket.emit("send_message", { message: count++})
  }

  useEffect(() => {
    socket.on("receive_message", (data) => {
      console.log('Contagem')
    })
  }, [socket])

return (
  <div className="App">
        <h1>Contador: </h1>
  </div>
  )
}

export default App;
