import React, { useState, useEffect } from 'react';
import './App.css';
import io from 'socket.io-client';

function App() {
  // Estado do contador
  const [count, setCount] = useState(0);

  // Conectando ao servidor WebSocket
  useEffect(() => {
    const socket = io('http://localhost:3001'); // Conecta-se ao servidor WebSocket

    // Escuta a atualização do contador
    socket.on('countUpdate', (newCount) => {
      setCount(newCount);
    });

    // Retorna uma função para desconectar o socket quando o componente for desmontado
    return () => socket.disconnect();
  }, []);

  // Funções para enviar os eventos de incremento e decremento para o servidor
  const increment = () => {
    const socket = io('http://localhost:3001');
    socket.emit('increment'); // Envia evento para incrementar
  };

  const decrement = () => {
    const socket = io('http://localhost:3001');
    socket.emit('decrement'); // Envia evento para decrementar
  };

  return (
    <div className="App">
      <h1>Contador: {count}</h1>
      <div className="buttons">
        <button onClick={increment}>+</button>
        <button onClick={decrement}>-</button>
      </div>
    </div>
  );
}

export default App;
